        static void Main(string[] args)
        {
            var ups = _input.Count(x => x.Equals('('));
            var downs = _input.Count(x => x.Equals(')'));

            var answer = ups - downs;

            Console.WriteLine($"The answer is {answer}");

            var current = 0;
            var counter = 0;
            foreach (var c in _input)
            {
                counter++;
                switch (c)
                {
                    case '(':
                        current++;
                        break;
                    case ')':
                        current--;
                        break;
                }

                if (current < 0)
                    break;
            }

            Console.WriteLine($"The basement is entered at position {counter}");
        }

